visitScheduleRule:
//SAMPLE RULE EXAMPLE
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });

  const moment = imports.moment;
  const _ = imports.lodash;
  const cancelReason = programEncounter.findCancelEncounterObservationReadableValue("Cancel reason");

  const earliestDate = moment(programEncounter.earliestVisitDateTime).add(1, 'month').startOf('month').toDate();
  let isEditScenario = programEncounter.programEnrolment.encounters.some((enc) => 
            enc.encounterType.name === 'Growth Monitoring' && 
            enc.voided === false && 
            imports.moment(enc.earliestVisitDateTime).isSame(earliestDate, 'day')
          );


    // greater than 5yrs children with any cancellation reason

    if (cancelReason && programEncounter.programEnrolment.individual.getAgeInYears() >= 5)
  return scheduleBuilder.getAll();

    if(programEncounter.programEnrolment.individual.getAgeInYears() < 5 && cancelReason != 'Death' && cancelReason != 'Migrated to other geography' && !isEditScenario) {
        scheduleBuilder.add({
            name: 'Growth Monitoring',
            encounterType: 'Growth Monitoring',
            earliestDate: earliestDate,
            maxDate:  moment(earliestDate).endOf('month').toDate()
        });
    }

  return scheduleBuilder.getAll();
};

validationRule:
//SAMPLE RULE EXAMPLE
'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const validationResults = [];
  //use imports.common.createValidationError('sample error') to create validation error and push it to validationResults
  const isBeforeScheduleDate = moment(programEncounter.cancelDateTime)
    .isBefore(moment(programEncounter.earliestVisitDateTime), 'day');

  if (isBeforeScheduleDate) {
    validationResults.push(
      imports.common.createValidationError("Visit cannot be cancelled before the scheduled date")
    );
  }
  return validationResults;
};
