"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const moment = imports.moment;
    const decisions = params.decisions;
    const decisionsList = [];


  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("3f11d680-4da0-43b9-a532-e080b855b9d6").containsAnswerConceptName("8ebbf088-f292-483e-9084-7de919ce67b7").matches();

    if(condition11 ){
    decisionsList.push({name: "To be monitored by QRT", value: ["Yes"]});  
}

    decisions.enrolmentDecisions.push(...decisionsList);
    decisions.encounterDecisions.push(...decisionsList);
    decisions.registrationDecisions.push(...decisionsList);
    return decisions;
};
