//SAMPLE RULE EXAMPLE
'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const validationResults = [];
  //use imports.common.createValidationError('sample error') to create validation error and push it to validationResults
  const isBeforeScheduleDate = moment(programEncounter.cancelDateTime)
    .isBefore(moment(programEncounter.earliestVisitDateTime), 'day');

  if (isBeforeScheduleDate) {
    validationResults.push(
      imports.common.createValidationError("Visit cannot be cancelled before the scheduled date")
    );
  }
  return validationResults;
};
