//SAMPLE RULE EXAMPLE
  "use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const decisions = params.decisions;

    const recommendationBuilder = new imports.rulesConfig.complicationsBuilder({
        programEncounter: programEncounter,
        complicationsConcept: "Recommendations"
    });

    recommendationBuilder.addComplication("Keep the baby warm")
        .when.valueInEncounter("Child Pulse").lessThan(60)
        .or.when.valueInEncounter("Child Pulse").greaterThan(100)
        .or.when.valueInEncounter("Child Respiratory Rate").lessThan(30)
        .or.when.valueInEncounter("Child Respiratory Rate").greaterThan(60)
    ;

    recommendationBuilder.addComplication("Keep the baby warm by giving mother's skin to skin contact and covering the baby's head, hands and feet with a cap, gloves and socks resp.")
        .when.valueInEncounter("Child Temperature").lessThan(97.5);

    recommendationBuilder.addComplication("Give exclusive breast feeding")
        .when.valueInEncounter("Child Temperature").lessThan(97.5);

    recommendationBuilder.addComplication("Give exclusive breast feeding")
        .when.encounterType.equals("Child PNC")
        .and.valueInEncounter("Is baby exclusively breastfeeding").containsAnswerConceptName("No");


 decisions.encounterDecisions.push(recommendationBuilder.getComplications());   


  const referralAdvice = new imports.rulesConfig.complicationsBuilder({
        programEncounter: programEncounter,
        complicationsConcept: "Refer to the hospital immediately for"
    });

    referralAdvice.addComplication("Child born Underweight")
        .when.valueInEncounter("Birth Weight").lessThan(2);

    referralAdvice.addComplication("Did not cry soon after birth")
        .when.valueInEncounter("Cried soon after birth").containsAnswerConceptName("No");

    referralAdvice.addComplication("Colour of child is Pale or Blue")
        .when.valueInEncounter("Colour of child").containsAnswerConceptName("Blue/pale");

    referralAdvice.addComplication("Reflex Absent")
        .when.valueInEncounter("Reflex").containsAnswerConceptName("Absent");

    referralAdvice.addComplication("Muscle tone Absent/Flexed arms and legs")
        .when.valueInEncounter("Muscle tone").containsAnyAnswerConceptName("Absent", "Flexed arms and legs");

    referralAdvice.addComplication("Low Pulse")
        .when.valueInEncounter("Child Pulse").lessThan(60);

    referralAdvice.addComplication("High Pulse")
        .when.valueInEncounter("Child Pulse").greaterThan(100);

    referralAdvice.addComplication("Low Temperature")
        .when.valueInEncounter("Child Temperature").lessThan(97.5);

    referralAdvice.addComplication("High Temperature")
        .when.valueInEncounter("Child Temperature").greaterThan(99.5);

    referralAdvice.addComplication("Low Respiratory Rate")
        .when.valueInEncounter("Child Respiratory Rate").lessThan(30);

    referralAdvice.addComplication("High Respiratory Rate")
        .when.valueInEncounter("Child Respiratory Rate").greaterThan(60);

    referralAdvice.addComplication("Icterus present")
        .when.valueInEncounter("Jaundice (Icterus)").containsAnswerConceptName("Present");

    referralAdvice.addComplication("Urine not passed for more than 48 hours after birth")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Duration in hours between birth and first urination").greaterThan(48);

    referralAdvice.addComplication("Urine not passed since birth")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child passed urine since birth").is.no;

    referralAdvice.addComplication("Meconium not passed for more than 48 hours after birth")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Duration in hours between birth and meconium").greaterThan(48);

    referralAdvice.addComplication("Meconium not passed since birth")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child passed meconium since birth").is.no;

    referralAdvice.addComplication("Child PNC cry related complaints")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC cry related complaints").containsAnswerConceptName("Cries Continuously", "Poor cry");

    referralAdvice.addComplication("Child PNC feeding related complaints")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC feeding related complaints").containsAnswerConceptName("Not sucking milk at all", "Vomiting");

    referralAdvice.addComplication("Child PNC urination related complaints")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC urination related complaints").containsAnswerConceptName("No urination", "Difficulty in urination");

    referralAdvice.addComplication("Child PNC stool related complaints")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC stool related complaints").containsAnswerConceptName("Blood in stools", "Loose stools", "Not passing stools");

    referralAdvice.addComplication("Child PNC activity related complaints")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC activity related complaints").containsAnswerConceptName("Sluggish movements", "Not sucking milk at all", "Convulsions", "Slow activity / lethargic", "Unconscious");

    referralAdvice.addComplication("Fever/Chills")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Fever/Chills").containsAnswerConceptName("Yes");

    referralAdvice.addComplication("Child PNC eye problems")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC eye problems").containsAnswerConceptName("Redness of eyes", "Swollen eyelids", "Discharge from eyes", "Icterus present");

    referralAdvice.addComplication("Child PNC skin problems")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC skin problems").containsAnswerConceptName("Blue/pale", "Umbilical redness and or discharge", "Umbilical Abscess", "Rash", "Wrinkled Skin", "Sunken fontanelle", "Skin blisters")
        .or.when.valueInEncounter("Child PNC skin problems").containsAnswerConceptName("Rash").and.ageInDays.is.greaterThan(3);

    referralAdvice.addComplication("Child PNC breathing problems")
        .when.encounterType.equals("Child PNC")
        .when.valueInEncounter("Child PNC breathing problems").containsAnswerConceptName("Chest indrawing", "Breathing too fast", "Breathing too slow", "Grunting noises while breathing");

    decisions.encounterDecisions.push(referralAdvice.getComplications());   

    const highRiskBuilder = new imports.rulesConfig.complicationsBuilder({
        programEncounter: programEncounter,
        complicationsConcept: "High Risk Conditions"
    });

    highRiskBuilder.addComplication("Child born Underweight")
        .when.valueInEncounter("Birth Weight").lessThan(2);

    highRiskBuilder.addComplication("Did not cry soon after birth")
        .when.valueInEncounter("Cried soon after birth").containsAnswerConceptName("No");

    highRiskBuilder.addComplication("Not Breast-fed within 1 hour of birth")
        .when.valueInEncounter("Breast feeding within 1 hour of birth").containsAnswerConceptName("No");

    highRiskBuilder.addComplication("Colour of child is Pale or Blue")
        .when.valueInEncounter("Colour of child").containsAnswerConceptName("Blue/pale")

    highRiskBuilder.addComplication("Reflex Absent")
        .when.valueInEncounter("Reflex").containsAnswerConceptName("Absent");

    highRiskBuilder.addComplication("Muscle tone Absent/Flexed arms and legs")
        .when.valueInEncounter("Muscle tone").containsAnyAnswerConceptName("Absent", "Flexed arms and legs");

    highRiskBuilder.addComplication("Low Pulse")
        .when.valueInEncounter("Child Pulse").lessThan(60);

    highRiskBuilder.addComplication("High Pulse")
        .when.valueInEncounter("Child Pulse").greaterThan(100);

    highRiskBuilder.addComplication("Low Temperature")
        .when.valueInEncounter("Child Temperature").lessThan(97.5);

    highRiskBuilder.addComplication("High Temperature")
        .when.valueInEncounter("Child Temperature").greaterThan(99.5);

    highRiskBuilder.addComplication("Low Respiratory Rate")
        .when.valueInEncounter("Child Respiratory Rate").lessThan(30);

    highRiskBuilder.addComplication("High Respiratory Rate")
        .when.valueInEncounter("Child Respiratory Rate").greaterThan(60);

    highRiskBuilder.addComplication("Icterus present")
        .when.valueInEncounter("Jaundice (Icterus)").containsAnswerConceptName("Present");

    decisions.encounterDecisions.push(highRiskBuilder.getComplications());   

       const zScoreGradeStatusMappingWeightForAge = {
    '1': 'Normal',
    '2': 'Moderately Underweight',
    '3': 'Severely Underweight'
};

const zScoreGradeStatusMappingHeightForAge = {
    '1': 'Normal',
    '2': 'Stunted',
    '3': 'Severely stunted'
};

const zScoreGradeStatusMappingWeightForHeight = [
    ["Severely wasted", -3],
    ["Wasted", -2],
    ["Normal", 1],
    ["Possible risk of overweight", 2],
    ["Overweight", 3],
    ["Obese", Infinity],
];

const weightForHeightStatus = function (zScore) {
    let found = _.find(zScoreGradeStatusMappingWeightForHeight, function (currentStatus) {
        return zScore <= currentStatus[1];
    });
    return found && found[0];
}

const getGradeforZscore = (zScore) => {
    let grade;
    if (zScore <= -3) {
        grade = 3;
    }
    else if (zScore > -3 && zScore < -2) {
        grade = 2;
    }
    else if (zScore >= -2) {
        grade = 1;
    }

    return grade;

}

const addIfRequired = (decisions, name, value) => {
    if (value === -0) value = 0;
    if (value !== undefined) decisions.push({name: name, value: value});
};

   const currentEncounterDateTime = programEncounter.encounterDateTime;
   const individual = programEncounter.programEnrolment.individual;

   const weight = programEncounter.findObservation("Weight") &&
                  programEncounter.findObservation("Weight").getValue();
   const height = programEncounter.findObservation("Height") &&     
                  programEncounter.findObservation("Height").getValue();

      const zScoresForChild = ruleServiceLibraryInterfaceForSharingModules.common.getZScore(individual, currentEncounterDateTime, weight, height);
      console.log('zScoresForChild',zScoresForChild);

    var wfaGrade = getGradeforZscore(zScoresForChild.wfa);
    console.log('zScoresForChild',getGradeforZscore(zScoresForChild.wfa));

    var wfaStatus = zScoreGradeStatusMappingWeightForAge[wfaGrade];

    var hfaGrade = getGradeforZscore(zScoresForChild.hfa);
    var hfaStatus = zScoreGradeStatusMappingHeightForAge[hfaGrade];

    var wfhStatus = weightForHeightStatus(zScoresForChild.wfh);

    addIfRequired(decisions.encounterDecisions, "Weight for age z-score", zScoresForChild.wfa);
    addIfRequired(decisions.encounterDecisions, "Weight for age Grade", wfaGrade);
    addIfRequired(decisions.encounterDecisions, "Weight for age Status", wfaStatus ? [wfaStatus] : []);

    addIfRequired(decisions.encounterDecisions, "Height for age z-score", zScoresForChild.hfa);
    addIfRequired(decisions.encounterDecisions, "Height for age Grade", hfaGrade);
    addIfRequired(decisions.encounterDecisions, "Height for age Status", hfaStatus ? [hfaStatus] : []);

    addIfRequired(decisions.encounterDecisions, "Weight for height z-score", zScoresForChild.wfh);
    addIfRequired(decisions.encounterDecisions, "Weight for Height Status", wfhStatus ? [wfhStatus] : []);

   const sd2Neg = ruleServiceLibraryInterfaceForSharingModules.common
                    .projectedSD2NegForWeight(individual, currentEncounterDateTime);
    const observations = programEncounter.programEnrolment.getObservationsForConceptName("Weight");
    const observationsSorted = _.orderBy(observations,
        encounter => imports.moment(encounter.encounterDateTime).unix(),
        ["desc"]
    );

    const pastObservation = _.find(observationsSorted, observation =>
        imports.moment(currentEncounterDateTime).diff(imports.moment(observation.encounterDateTime), "months", true) >= 3.0
    );

    if (pastObservation) {
        const pastSD2Neg = ruleServiceLibraryInterfaceForSharingModules.common
        .projectedSD2NegForWeight(individual, pastObservation.encounterDateTime);

        const pastWeight = pastObservation.obs;
        const falteringInPast = pastWeight < pastSD2Neg;
        const falteringStatus = falteringInPast && weight < sd2Neg ? "Yes" : "No";
        addIfRequired(decisions.encounterDecisions, "Growth Faltering Status", [falteringStatus]);
    } else {
        const falteringStatus = "No";
        addIfRequired(decisions.encounterDecisions, "Growth Faltering Status", [falteringStatus]);
    }


    return decisions;
};
