//SAMPLE RULE EXAMPLE
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });
  //add new visit schedule object to scheduleBuilder
  let visitDate = imports.moment(programEncounter.encounterDateTime).add(30, 'days').toDate();

  scheduleBuilder
    .add({
      name: "Mental Health followup",
      encounterType: "Mental Health followup",
      earliestDate: visitDate,
      maxDate: imports.moment(visitDate).add(20, 'days').toDate()
    });

  return scheduleBuilder.getAll();
};
