  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter, formElement})
    .when.valueInEncounter("0a970d54-f2f3-4827-97ae-cca3956f4c5c").defined.matches();

  if (condition11) {
    // Format date as YYYY-MM-DD (compatible with OpenMRS)
    // change this to below value = imports.moment().toDate();
    value = moment().format("YYYY-MM-DD");
  }

  return new imports.rulesConfig.FormElementStatus(
    formElement.uuid, visibility, value, answersToSkip, validationErrors
  );
};

'use strict';
({ params, imports }) => {
  const programEncounter = params.entity;
  const formElement = params.formElement;

  // Fetch observations
  let wbc = programEncounter.findObservation("WBC count");
  let neutrophil = programEncounter.findObservation("SCD DC Neutrophil");

  wbc = wbc && wbc.getValue();
  neutrophil = neutrophil && neutrophil.getValue();

  console.log('WBC count', wbc);
  console.log('Neutrophil %', neutrophil);

  let value = '';

  if (_.isFinite(wbc) && _.isFinite(neutrophil)) {
    value = (wbc * neutrophil) / 100;
    value = Math.round(value); // optional
  }

  return new imports.rulesConfig.FormElementStatus(
    formElement.uuid,
    true,
    value
  );
};

