'use strict';
({ params, imports }) => {
  const programEncounter = params.entity;
  const formElement = params.formElement;
  const FormElementStatus = imports.rulesConfig.FormElementStatus;

  // Set default status values
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  // UUID for the "SCD Hydroxyurea when started" concept
  const HydroxyureastartedConceptUUID = "01a279d6-1bc7-4ccf-8d00-676195f78ebe";

  // Fetch the latest past encounter that has height recorded
  const programEnrolment = programEncounter.programEnrolment;
  const hydroxStartObs =
    programEnrolment && programEnrolment.findLatestObservationInEntireEnrolment
      ? programEnrolment.findLatestObservationInEntireEnrolment(HydroxyureastartedConceptUUID)
      : null;

  if (hydroxStartObs) {
    if (typeof hydroxStartObs.getValue === 'function') {
      const obsValue = hydroxStartObs.getValue();
      if (obsValue !== undefined && obsValue !== null) {
        value = obsValue; // Set the previously recorded SCD Hydroxyurea when started
      }
    } else if (hydroxStartObs.value !== undefined && hydroxStartObs.value !== null) {
      value = hydroxStartObs.value; // Set the previously recorded SCD Hydroxyurea when started
    }
  }

  return new FormElementStatus(
    formElement.uuid,
    visibility,
    value,
    answersToSkip,
    validationErrors
  );
};

