'use strict';
({params, imports}) =>  {
    const summaries = [];
    const programEnrolment = params.programEnrolment;
    //value can be fetched from entire enrolment using programEnrolment.findObservationInEntireEnrolment(CONCEPT_NAME);
    //value can be fetched from entire enrolment using 
    const Diagnosis= programEnrolment.findObservationInEntireEnrolment("Diagnosis_MH");
    if (Diagnosis) {
     summaries.push({name: 'Diagnosis_MH', value: Diagnosis.getValue()});
    }    
        const Medicines1 = programEnrolment.findObservationInEntireEnrolment("Medicines are being given");
    if (Medicines1) {
     summaries.push({name: 'Medicines are being given', value: Medicines1.getValue()});
    }  
    const Medicines = programEnrolment.findLatestObservationInEntireEnrolment("Medicines which are administered now");
    if (Medicines) {
     summaries.push({name: 'Medicines which are administered now', value: Medicines.getValue()});
    }  
    const color = programEnrolment.findObservationInEntireEnrolment("Patient colour coding");
    if (color) {
     summaries.push({name: 'Patient colour coding - Enrolment', value: color.getValue()});
    }

  //
    const encounters = programEnrolment.getEncountersOfType('Mental Health followup');
   // console.log('encounters---->',encounters);

    let enc1 = _.chain(encounters)
    	.filter((enc) => enc.encounterDateTime)
    	.orderBy((enc) => enc.encounterDateTime, 'desc')
    	.nth(0)
    	.value();


    let enc2 = _.chain(encounters)
    	.filter((enc) => enc.encounterDateTime)
    	.orderBy((enc) => enc.encounterDateTime, 'desc')
    	.filter((enc) => !_.isNil(enc.encounterDateTime))
    	.nth(1)
    	.value();

    let enc3 = _.chain(encounters)
    	.filter((enc) => enc.encounterDateTime)
    	.orderBy((enc) => enc.encounterDateTime, 'desc')
    	.filter((enc) => !_.isNil(enc.encounterDateTime))
    	.nth(2)
    	.value();

    let enc1Value = '',enc2Value = '',enc3Value = '';
    if (enc1) {    	
    	enc1Value = enc1.getObservationReadableValue('Patient colour coding');
    }
    if (!_.isEmpty(enc1Value)) {
      summaries.push({name: 'Color code for last first visit', value: enc1Value});
    }    
    if (enc2) {
    		enc2Value = enc2.getObservationReadableValue('Patient colour coding');
    }
     if (!_.isEmpty(enc2Value)) {
      summaries.push({name: 'Color code for last second visit', value: enc2Value});
    }
    if (enc3) {
    		enc3Value = enc3.getObservationReadableValue('Patient colour coding');
    }
     if (!_.isEmpty(enc3Value)) {
      summaries.push({name: 'Color code for last third visit', value: enc3Value});
    }

    return summaries;
};

