decisionRule:
"use strict";
({params, imports}) => {
    const encounter = params.entity;
    const decisions = params.decisions;
    const moment = imports.moment;
    const encounterDecisions = [];

    const encounterDateForEditCase = encounter.getObservationValue("1f4bc226-9e3f-42c9-a332-2d81cd8e64f7");

    if (!encounterDateForEditCase) {
      let encDateTime = moment().format("YYYY-MM-DD HH:mm:ss");

      if (encDateTime) {
        encounterDecisions.push({ name: "Encounter DateTime", value: encDateTime });
      }
      decisions.encounterDecisions.push(...encounterDecisions);
    }

    return decisions;
};

validationRule:
//SAMPLE RULE EXAMPLE
'use strict';
({params, imports}) => {
  const encounter = params.entity;
  const validationResults = [];
  const moment = imports.moment;

  const encounterDateForEditCase = encounter.getObservationValue("1f4bc226-9e3f-42c9-a332-2d81cd8e64f7");
  const encounterDate = encounter.encounterDateTime;

  if (!encounterDateForEditCase) {
    if (encounterDate) {
      const today = moment().startOf('day');
      const encDate = moment(encounterDate).startOf('day');

      if (!encDate.isSame(today, 'day')) {
        validationResults.push(
          imports.common.createValidationError("Encounter Date should be today's date.")
        );
      }
    }
  } else {
    const encDateEdit = moment(encounterDate).startOf('day');
    const encDateOriginal = moment(encounterDateForEditCase).startOf('day');

    if (!encDateEdit.isSame(encDateOriginal, 'day')) {
      validationResults.push(
        imports.common.createValidationError("Encounter Date cannot be changed.")
      );
    }
  }
  return validationResults;
};
