'use strict';
({params, imports}) => {
  const programEnrolment = params.entity;
  const individual = programEnrolment.individual;
  const db = params.db;
  const formElement = params.formElement;
  const _ = imports.lodash;

  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];
  let answersToShow = [];

  const individualAddressUuid = individual.lowestAddressLevel && individual.lowestAddressLevel.uuid;

  if (!individualAddressUuid) {
    validationErrors.push("Individual's address information is missing");
    return new imports.rulesConfig.FormElementStatus(
      formElement.uuid, 
      visibility, 
      value, 
      answersToSkip, 
      validationErrors, 
      answersToShow
    );
  }

  const courtQuery = `
    voided = false 
    AND subjectType.uuid = "cadc8363-b121-4cd4-bbbf-5bee4fd620d1"
  `;

  const allCourts = db.objects("Individual").filtered(courtQuery);

  const filteredCourts = allCourts.filter(court => {
    const jurisdictionObs = court.findObservation('feb3dffb-8e6e-4e64-a9f2-d90bf5689cb6');
    if (!jurisdictionObs || !jurisdictionObs.valueJSON) return false;

    const valueData = JSON.parse(jurisdictionObs.valueJSON);
    const courtJurisdictionUuid = valueData.value || valueData.answer;

    return courtJurisdictionUuid === individualAddressUuid;
  });

  answersToShow = filteredCourts.map(court => court.uuid);

  const selectedCourt = programEnrolment.getObservationValue("03d822c9-0e66-4ae4-85e7-aecb5024a171");

  if (selectedCourt && answersToShow.length > 0 && !answersToShow.includes(selectedCourt)) {
    validationErrors.push("The selected court is not within the jurisdiction of the individual's address.");
  }

  return new imports.rulesConfig.FormElementStatus(
    formElement.uuid, 
    visibility, 
    value, 
    answersToSkip, 
    validationErrors, 
    answersToShow
  );
};

