validationRule:
'use strict';
({params, imports}) => {
  const programEnrolment = params.entity;
  const moment = imports.moment;
  const validationResults = [];

  const enrolmentDateForEditCase = programEnrolment.getObservationValue("3ecdf82a-adf3-4e29-aa3f-244db1109a2d");
  const enrolmentDate = programEnrolment.enrolmentDateTime;

  if (!enrolmentDateForEditCase) {
    if (enrolmentDate) {
      const today = moment().startOf('day');
      const enrlDate = moment(enrolmentDate).startOf('day');

      if (!enrlDate.isSame(today, 'day')) {
        validationResults.push(
          imports.common.createValidationError("Enrolment Date should be today's date.")
        );
      }
    }
  } else {
    const enrlDateEdit = moment(enrolmentDate).startOf('day');
    const enrlDateOriginal = moment(enrolmentDateForEditCase).startOf('day');

    if (!enrlDateEdit.isSame(enrlDateOriginal, 'day')) {
      validationResults.push(
        imports.common.createValidationError("Enrolment Date cannot be changed.")
      );
    }
  }

  return validationResults;
};
