decisionRule:
//SAMPLE RULE EXAMPLE
  "use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const decisions = params.decisions;
    const moment = imports.moment;
    const encounterDecisions = [];

    const encounterDateForEditCase = programEncounter.getObservationValue("1f4bc226-9e3f-42c9-a332-2d81cd8e64f7");

    if (!encounterDateForEditCase) {
      let encDateTime = moment().format("YYYY-MM-DD HH:mm:ss");

      if (encDateTime) {
        encounterDecisions.push({ name: "Encounter DateTime", value: encDateTime });
      }
      decisions.encounterDecisions.push(...encounterDecisions);
    }
    return decisions;
};

validationRule:
'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const validationResults = [];
  const formElement = params.formElement;
  const _ = imports.lodash;
  let validationErrors = [];
  const moment = imports.moment;


  let documentDetails = programEncounter.findGroupedObservation("6a66028e-e11c-4b47-a94d-695d1b232577");
  if(documentDetails && documentDetails.length > 0){

    for(let i = 0;i<documentDetails.length;i++){
      let currentQuestionGroup = documentDetails[i]
      let impactDescription= currentQuestionGroup.findObservationByConceptUUID("bed28fd5-e245-42d5-b547-6b100a90c1d9");
      let monetaryImpact=  currentQuestionGroup.findObservationByConceptUUID("35d197f1-e61c-4efe-a8a9-7d65f741edad");


      if((impactDescription=== null || impactDescription=== undefined) && (monetaryImpact=== null || monetaryImpact=== undefined)){
        let validationError = imports.common.createValidationError('Please answer either Impact Description OR Monetary Impact Question');
        validationResults.push(validationError);
      }
    }
  }  

  const encounterDateForEditCase = programEncounter.getObservationValue("1f4bc226-9e3f-42c9-a332-2d81cd8e64f7");
  const encounterDate = programEncounter.encounterDateTime;

  if (!encounterDateForEditCase) {
    if (encounterDate) {
      const today = moment().startOf('day');
      const encDate = moment(encounterDate).startOf('day');

      if (!encDate.isSame(today, 'day')) {
        validationResults.push(
          imports.common.createValidationError("Encounter Date should be today's date.")
        );
      }
    }
  } else {
    const encDateEdit = moment(encounterDate).startOf('day');
    const encDateOriginal = moment(encounterDateForEditCase).startOf('day');

    if (!encDateEdit.isSame(encDateOriginal, 'day')) {
      validationResults.push(
        imports.common.createValidationError("Encounter Date cannot be changed.")
      );
    }
  }

  return validationResults;
};
