'use strict';
({params, imports}) => {
  const individual = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;

  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  let visitDateStr = individual.getObservationReadableValue("Visit Date");

  if (visitDateStr) {
    const visitDate = moment(visitDateStr, "YYYY-MM-DD"); // adjust format if needed
    const today = moment().startOf('day');

    if (visitDate.isAfter(today)) {
      validationErrors.push("Visit Date cannot be a future date.");
    }
  }

  return new imports.rulesConfig.FormElementStatus(
    formElement.uuid,
    visibility,
    value,
    answersToSkip,
    validationErrors
  );
};

