formElementGroups.rule:
'use strict';
({params, imports}) => {
    const individual = params.entity;
    const moment = imports.moment;
    const formElementGroup = params.formElementGroup;
    const _ = imports.lodash;
    let visibility = true;

    return formElementGroup.formElements.map((formElement) => {
        if (individual) {
            const villageNameUUID = individual.getObservationReadableValue("Address Details - Village");
            let villageName = '';
            const db = params.db;
            if(villageNameUUID){
              const locationResults = db.objects("LocationHierarchy").filtered('uuid == $0', villageNameUUID);

              if (locationResults && locationResults.length > 0) {
                const locationObj = locationResults[0];
                villageName = locationObj.name; 
              }
            } 

            const otherSpaceNameUUID = individual.getObservationReadableValue("Address Details - Other Space");
            let otherSpaceName = '';

            if(otherSpaceNameUUID){
              const locationResults = db.objects("LocationHierarchy").filtered('uuid == $0', otherSpaceNameUUID);

              if (locationResults && locationResults.length > 0) {
                const locationObj = locationResults[0];
                otherSpaceName = locationObj.name; 
              }
            } 

            const anyaSthalName = individual.getObservationReadableValue("Anya Sthal");
            const visitDateStr = individual.getObservationReadableValue("Visit Date");

            let visitDateFormatted = '';
            if (visitDateStr) {
                const visitDate = moment(visitDateStr, "DD-MM-YYYY"); // use correct format
                if (visitDate.isValid()) {
                    visitDateFormatted = visitDate.format("DD-MM-YYYY");
                }
            }

            // Build name parts conditionally
            let nameParts = [];
            if (villageName) nameParts.push(villageName);
            if (otherSpaceName) nameParts.push(otherSpaceName);
            if (anyaSthalName) nameParts.push(anyaSthalName);
            if (visitDateFormatted) nameParts.push(visitDateFormatted);

            const outreachName = nameParts.length > 0 ? `Outreach: ${nameParts.join(', ')}` : "Outreach: ";

            individual.firstName = outreachName;
            individual.name = outreachName;
        }

        return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, null);
    });
};

formElements.rule:
'use strict';
({ params, imports }) => {
  const individual = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];
  let answersToShow = [];

  const condition11 = new imports.rulesConfig.RuleCondition({ individual, formElement })
    .when.valueInRegistration("004708d0-693b-43b7-9e20-06932d15f7f6")
    .containsAnswerConceptName("d045b9b5-0d0f-4ead-82c4-7ff05bf4efa6")
    .matches();

  visibility = condition11;

  if(visibility){
    const adminUnit = individual.lowestAddressLevel;
    const currentAddressUUID = adminUnit && adminUnit.uuid;

    const db = params.db;
    const allCampaigns = db.objects("Individual").filtered(
      `voided = false AND subjectType.name = "Campaign"`
    );

    const validCampaigns = allCampaigns.filter(function (ind) {
      return ind.lowestAddressLevel && ind.lowestAddressLevel.uuid === currentAddressUUID;
    });

    const validCampaignUUIDs = validCampaigns.map(function (ind) {
      return ind.uuid;
    });

    if (validCampaignUUIDs.length > 0) {
      answersToShow = validCampaignUUIDs;
    } else {
      answersToShow = [""];
      validationErrors.push("No Campaigns present to select from. Please register a Campaign before proceeding with Outreach registration.");
    }
  }

  return new imports.rulesConfig.FormElementStatus(
    formElement.uuid,
    visibility,
    value,
    answersToSkip,
    validationErrors,
    answersToShow
  );
};

