'use strict';

({ params, imports }) => {
  const individual = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;

  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  const selectedDate = individual.getObservationValue('ff308558-e951-46bf-8115-c41bf16b02da');
  const currentDate = moment().startOf('day');

  if (selectedDate) {
    const selectedMoment = moment(selectedDate).startOf('day');
    if (selectedMoment.isAfter(currentDate)) {
      validationErrors.push('Invalid date: Future dates are not allowed');
    }
  }

  return new imports.rulesConfig.FormElementStatus(
    formElement.uuid,
    visibility,
    value,
    answersToSkip,
    validationErrors
  );
};

