'use strict';
({ params, imports }) => {
    const individual = params.entity;
    const moment = imports.moment;
    const formElementGroup = params.formElementGroup;
    const _ = imports.lodash;
    let visibility = true;

    return formElementGroup.formElements.map((formElement) => {

       individual.firstName = `Training_`;
        if (individual) {
            const typeOfTrainingUuid = "c7afa395-baae-4434-b8f7-b938b78051bd";
            const natureOfTrainingUuid = "302ce25a-1fe7-4c5a-9a31-e1e54009187b";
            const titleOfTrainingUuid = "b4b83d36-7386-4b7c-bf98-0a13a30f0559";
            const visitDateUuid = "ff308558-e951-46bf-8115-c41bf16b02da";

            const typeOfTraining = individual.getObservationReadableValue(typeOfTrainingUuid);
            const natureOfTraining = individual.getObservationReadableValue(natureOfTrainingUuid);
            const titleOfTraining = individual.getObservationReadableValue(titleOfTrainingUuid);
            const visitDateRaw = individual.getObservationValue(visitDateUuid); 

            const formattedDate = visitDateRaw ? moment(visitDateRaw).format("DD-MM-YYYY") : '';

            if (typeOfTraining === "Internal") {
                individual.firstName = `Training_${typeOfTraining}_${natureOfTraining || ''}_${formattedDate}`;
                individual.name = `Training_${typeOfTraining}_${natureOfTraining || ''}_${formattedDate}`;
            } else if (typeOfTraining === "External") {
                individual.firstName = `Training_${typeOfTraining}_${titleOfTraining || ''}_${formattedDate}`;
                individual.name = `Training_${typeOfTraining}_${titleOfTraining || ''}_${formattedDate}`;
            }
        }

        return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, null);
    });
};

decisionRule:
"use strict";
({params, imports}) => {
    const individual = params.entity;
    const moment = imports.moment;
    const decisions = params.decisions;
    const enrolmentDecisions = [];
    const encounterDecisions = [];
    const registrationDecisions = [];

  const condition11 = new imports.rulesConfig.RuleCondition({individual}).when.valueInRegistration("c7afa395-baae-4434-b8f7-b938b78051bd").containsAnswerConceptName("3feb2dac-7f40-49e7-82c8-a5c55b3feb8a").matches();

  const condition12 = new imports.rulesConfig.RuleCondition({individual}).when.valueInRegistration("c7afa395-baae-4434-b8f7-b938b78051bd").containsAnswerConceptName("f882e2b6-52e1-43cf-a45b-215e76ff05eb").matches();

  const condition13 = new imports.rulesConfig.RuleCondition({individual}).when.valueInRegistration("387e72d2-1ced-4032-b273-ea3ef5c5a159").containsAnswerConceptName("9d91df97-79f6-4e42-8d61-19d8926edd9f").matches();

  const condition14 = new imports.rulesConfig.RuleCondition({individual}).when.valueInRegistration("387e72d2-1ced-4032-b273-ea3ef5c5a159").containsAnswerConceptName("02510e16-715b-4081-8165-af72278e9609").matches();

    if(condition11 ){
    registrationDecisions.push({name: "Type of training decision", value: ["Internal"]});  
}
    if(condition12 ){
    registrationDecisions.push({name: "Type of training decision", value: ["External"]});  
}
    if(condition13 ){
    registrationDecisions.push({name: "Whether phased Decision", value: ["Single event"]});  
}
    if(condition14 ){
    registrationDecisions.push({name: "Whether phased Decision", value: ["Phased event"]});  
}

    let createdByName = params.user.name;
    let createdByUUID = params.user.uuid;
    let createdByUsername = params.user.username;

    const registrationDateForEditCase = individual.getObservationValue("7b1a1540-bfd5-484d-987f-4d6f3635a448");
    if (!registrationDateForEditCase) {
      let regDateTime = moment().format("YYYY-MM-DD HH:mm:ss");
      if (regDateTime) {
        registrationDecisions.push({ name: "Registration DateTime", value: regDateTime });
      }
    }

    if(createdByName){
      registrationDecisions.push({name: "Created By Name", value: createdByName});  
    }

    if(createdByUsername){
      registrationDecisions.push({name: "Created By Username", value: createdByUsername});    
    }

    decisions.enrolmentDecisions.push(...enrolmentDecisions);
    decisions.encounterDecisions.push(...encounterDecisions);
    decisions.registrationDecisions.push(...registrationDecisions);
    return decisions;
};

validationRule:
'use strict';
({ params, imports }) => {
  const individual = params.entity;
  const moment = imports.moment;
  const validationResults = [];

  const registrationDateForEditCase = individual.getObservationValue("7b1a1540-bfd5-484d-987f-4d6f3635a448");
  const registrationDate = individual.registrationDate;

  if (!registrationDateForEditCase) {
    if (registrationDate) {
      const today = moment().startOf('day');
      const regDate = moment(registrationDate).startOf('day');

      if (!regDate.isSame(today, 'day')) {
        validationResults.push(
          imports.common.createValidationError("Registration Date should be today's date.")
        );
      }
    }
  } else {
    const regDateEdit = moment(registrationDate).startOf('day');
    const regDateOriginal = moment(registrationDateForEditCase).startOf('day');

    if (!regDateEdit.isSame(regDateOriginal, 'day')) {
      validationResults.push(
        imports.common.createValidationError("Registration Date cannot be changed.")
      );
    }
  }

  return validationResults;
};
