formElementGroups.rule:
'use strict';
({ params, imports }) => {
    const individual = params.entity;
    const moment = imports.moment;
    const formElementGroup = params.formElementGroup;
    const _ = imports.lodash;
    let visibility = true;

    return formElementGroup.formElements.map((formElement) => {
        if (individual) {
            const nameOfVolunteer = individual.getObservationReadableValue('44fc71bc-c6e2-4680-b514-5473f9510faf');
            const addressOfVolunteer = individual.lowestAddressLevel.name;

            if (!nameOfVolunteer && !addressOfVolunteer) {
                individual.firstName = `Volunteer_`;
                individual.name = `Volunteer_`;

            } else{
                 individual.firstName = `Volunteer_${nameOfVolunteer}_${addressOfVolunteer}`;
                  individual.name = `Volunteer_${nameOfVolunteer}_${addressOfVolunteer}`;

            }
        }

        return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, null);
    });
};

