   if(numberOfHyvas > 0)
   capacityOfHyva = encounter.getObservationReadableValue('Capacity of Hyva/Dumper in cu.m.');

  const familyMembers = encounter.getObservationReadableValue('Number of family members who took silt');

 // if(numberOfTrolleys > 0)
 //  value =  numberOfTrolleys * 2.8 ;
 // else if(numberOfHyvas > 0)
 //  value = ( numberOfHyvas * capacityOfHyva);

    value = (numberOfTrolleys * 2.8)  + (numberOfHyvas * capacityOfHyva);

  let visibility = false;

 if(familyMembers > 0)
  visibility = true;

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value.toFixed(2));
};

