'use strict';
({params, imports}) => {
    const programEncounter = params.entity;
    const moment = imports.moment;
    const formElementGroup = params.formElementGroup;
    const _ = imports.lodash;
    let visibility = true;
    return formElementGroup.formElements.map((formElement) => {

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter, formElement}).when.latestValueInPreviousEncounters("2efee2c2-38cf-457e-bc9a-be3e4a485bc7").containsAnswerConceptName("5310e8b2-7d43-4a25-ab3d-59c2e5a16801").or.when.latestValueInAllEncounters("d0a2adc1-03c9-4040-9e5d-681276216dbb").containsAnswerConceptName("04bb1773-c353-44a1-a68c-9b448e07ff70").or.when.latestValueInPreviousEncounters("2efee2c2-38cf-457e-bc9a-be3e4a485bc7").notDefined.matches();

        visibility = condition11 ;

        return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, null);
    });
};

visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("fc8f7481-74f2-458d-ade5-9eecc5c875aa").defined.matches();

  if(condition11 ){
    const earliestDate = moment(programEncounter.encounterDateTime).add(25, 'days').toDate();
    const maxDate = moment(programEncounter.encounterDateTime).add(35, 'days').toDate();
    scheduleBuilder.add({name: "Epilepsy Followup", encounterType: "Epilepsy Followup", earliestDate, maxDate});  
}

  return scheduleBuilder.getAll();
};
