visitScheduleRule:
"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const moment = imports.moment;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});
    const currentDate = moment();

    const ruleCondition = new imports.rulesConfig.RuleCondition({programEncounter});
    const isLmpPresent = ruleCondition.when.valueInEnrolment('Last menstrual period').defined.matches();

    const hasDelivery = programEncounter.programEnrolment.encounters.some(encounter =>
        encounter.encounterType.name === "Delivery Encounter" &&
        encounter.encounterDateTime &&
        !encounter.voided &&
        !encounter.cancelDateTime
    );
    const programExited = programEncounter.programEnrolment.programExitDateTime;

    if (isLmpPresent && !programExited) {
        const lmpDate = moment(programEncounter.findObservationInEntireEnrolment('Last menstrual period').getValue());
        const trimesterOfRegistration = programEncounter.getObservationReadableValue('Trimester of Registration in govt ANC program');
        const currentEncounterName = programEncounter.name;
        const ancSchedules = [
            {current: "ANC Follow-up Visit 1", next: "ANC Follow-up Visit 2", days: 175, overdue: 189},
            {current: "ANC Follow-up Visit 2", next: "ANC Follow-up Visit 3", days: 235, overdue: 250},
            {current: "ANC Follow-up Visit 3", next: "ANC Follow-up Visit 4", days: 260, overdue: 275},
        ];
        const nextVisit = ancSchedules.find(visit => visit.current === currentEncounterName);
        if (nextVisit && !hasDelivery) {
            scheduleBuilder.add({
                name: nextVisit.next,
                encounterType: "ANC Follow-up Visit",
                earliestDate: lmpDate.clone().add(nextVisit.days, 'days').toDate(),
                maxDate: lmpDate.clone().add(nextVisit.overdue, 'days').toDate()
            });
        }
        console.log('hasDelivery2',  hasDelivery, nextVisit, currentEncounterName);
        if (currentEncounterName === "ANC Follow-up Visit 3" && !hasDelivery) {
            scheduleBuilder.add({
                name: "Delivery Encounter",
                encounterType: "Delivery Encounter",
                earliestDate: lmpDate.clone().add(250, 'days').toDate(),
                maxDate: lmpDate.clone().add(280, 'days').toDate()
            });
        }
    }

    const highRiskConditions = [
        {type: "enrolment", concept: "Height", operator: "lessThan", value: 145},
        {type: "enrolment", concept: "BMI", operator: "lessThan", value: 18.5},
        {type: "enrolment", concept: "Number of abortions", operator: "greaterThan", value: 0},
        {type: "enrolment", concept: "Number of stillbirths", operator: "greaterThan", value: 0},
        {type: "enrolment", concept: "Complications in past pregnancy", operator: "containsAnswerConceptNameOtherThan", value: "None"},
        {type: "enrolment", concept: "Other High-Risk Condition in previous pregnancy", operator: "defined"},
        {type: "enrolment", concept: "Complications in Current Pregnancy", operator: "containsAnswerConceptNameOtherThan", value: "None"},
        {type: "enrolment", concept: "Other High Risk Condition in current pregnancy", operator: "defined"},
        {type: "enrolment", concept: "Lifestyle Factors", operator: "containsAnswerConceptNameOtherThan", value: "None"},
        {type: "enrolment", concept: "Medical history", operator: "containsAnswerConceptNameOtherThan", value: "None"},
        {type: "enrolment", concept: "Other Chronic Condition", operator: "defined"},
        {type: "anc", concept: "High Risk Pregnancy?", operator: "containsAnswerConceptName", value: "Yes"},
        {type: "anc", concept: "HIV/AIDS Diagnosis", operator: "containsAnswerConceptName", value: "Positive"},
        {type: "anc", concept: "Sonography Conducted?", operator: "containsAnswerConceptName", value: "Abnormal"},
        {type: "anc", concept: "HbsAg", operator: "containsAnswerConceptName", value: "Positive"},
        {type: "anc", concept: "Malaria Test", operator: "containsAnswerConceptName", value: "Positive"},
        {type: "anc", concept: "Blood group", operator: "containsAnswerConceptName", value: ["A-", "B-", "O-", "AB-"]},
        {type: "anc", concept: "Hemoglobin genotype", operator: "containsAnswerConceptName", value: ["SS", "Beta Thal", "S-Beta thal"]},
        {type: "anc", concept: "Thyroid Function", operator: "containsAnswerConceptName", value: ["Hypothyroidism", "Hyperthyroidism"]},
        {type: "anc", concept: "Hemoglobin Level", operator: "lessThan", value: 7},
        {type: "anc", concept: "OGTT", operator: "containsAnswerConceptName", value: "Abnormal"},
        {type: "anc", concept: "VDRL", operator: "containsAnswerConceptName", value: "Positive - Syphlis"},
    ];

    const checkCondition = (condition) => {
        const ruleCheck = condition.type === "enrolment"
            ? ruleCondition.when.valueInEncounter(condition.concept)
            : ruleCondition.when.latestValueInAllEncounters(condition.concept);
        if (condition.operator === "defined") {
            return ruleCheck.defined.matches();
        } else if (Array.isArray(condition.value)) {
            return condition.value.some(val => ruleCheck[condition.operator](val).matches());
        } else {
            return ruleCheck[condition.operator](condition.value).matches();
        }
    };

    const isHighRisk = highRiskConditions.some(checkCondition);

    const hasHighRiskPregnancyEnrolment = programEncounter.programEnrolment.getEncounters().some(enc => enc.name === 'MH High Risk Pregnancy Enrolment' && !enc.cancelDateTime);

    if (isHighRisk && !hasDelivery && !hasHighRiskPregnancyEnrolment && !programExited) {
        scheduleBuilder.add({
            name: "MH High Risk Pregnancy Enrolment",
            encounterType: "High Risk Enrolment",
            earliestDate: moment(programEncounter.encounterDateTime).add(3, 'days').toDate(),
            maxDate: moment(programEncounter.encounterDateTime).add(7, 'days').toDate()
        });
    }

    return scheduleBuilder.getAll();
};

