'use strict';
({params, imports}) => {
    const programEncounter = params.entity;
    const formElement = params.formElement;
    const _ = imports.lodash;

    let visibility = true;
    let value = null;
    let answersToSkip = [];
    let validationErrors = [];

    // Fetch weight value
    let weightObservation = programEncounter.findObservation('75b1656e-2777-4753-9612-ce03a766a5e1');
    let weight = weightObservation ? parseFloat(weightObservation.getReadableValue()) : null;

    // Fetch height value
    let heightObservation = programEncounter.findObservation('d883d5fe-e17d-4136-b989-089fa0295e34');
    let heightInCm = heightObservation ? parseFloat(heightObservation.getReadableValue()) : null;

    if (weight && heightInCm) {
        // Calculate BMI
        const heightInMeters = heightInCm / 100;
        const bmi = (weight / (heightInMeters * heightInMeters)).toFixed(2);

        // Update the value or show BMI message
        value = bmi;
    } else if (!weight || !heightInCm) {
        validationErrors.push("Please ensure both Weight and Height are entered to calculate BMI.");
    }

    return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};


