'use strict';
({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;

  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  // Define conditions
  const condition11 = new imports.rulesConfig.RuleCondition({ programEncounter, formElement }).when.ageInYears.lessThan(18).matches();
  const condition12 = new imports.rulesConfig.RuleCondition({ programEncounter, formElement }).when.ageInYears.greaterThan(35).matches();
  const condition13 = new imports.rulesConfig.RuleCondition({ programEncounter, formElement }).when.valueInEncounter("d883d5fe-e17d-4136-b989-089fa0295e34").lessThan(145).matches();
  const condition14 = new imports.rulesConfig.RuleCondition({ programEncounter, formElement }).when.valueInEncounter("7ac0d759-c50d-4971-88e0-84274224c839").lessThan(18.5).matches();
  const condition15 = new imports.rulesConfig.RuleCondition({ programEncounter, formElement }).when.valueInEncounter("dc2c23e9-19ad-471f-81d1-213069ccc975").greaterThan(4).matches();
  const condition16 = new imports.rulesConfig.RuleCondition({ programEncounter, formElement }).when.valueInEncounter("38b9986b-76e8-4015-ae51-48152b1cd42c").greaterThan(0).matches();


  // Collect matched conditions into an array
  let matchedValues = [];
  if (condition11) matchedValues.push("Age < 18");
  if (condition12) matchedValues.push("Age > 35");
  if (condition13) matchedValues.push("Height < 145");
  if (condition14) matchedValues.push("BMI < 18.5");
  if (condition15) matchedValues.push("Multigravida > 4");
  if (condition16) matchedValues.push("Abortions > 0");

  // Join multiple values into a single string
  if (matchedValues.length > 0) {
    value = matchedValues.join(", "); // Example output: "Age < 18, Height < 145"
  }

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

