"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const moment = imports.moment;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});
    const currentDate = moment();
    const isLmpPresent = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEnrolment('Last menstrual period').defined.matches();
    const hasDelivery = programEncounter.programEnrolment.encounters.some(encounter =>
        encounter.encounterType.name === "Delivery Encounter" &&
        encounter.encounterDateTime &&
        !encounter.voided &&
        !encounter.cancelDateTime
    );
    const programExited = programEncounter.programEnrolment.programExitDateTime;

    // Get age from individual registration
    const age = programEncounter.programEnrolment.individual.getAgeInYears();
    const isAgeHighRisk = age < 18 || age > 35;

    const hasANCFollowup1 = programEncounter.programEnrolment.getEncounters().some(enc => enc.name === 'ANC Follow-up Visit 1' && !enc.cancelDateTime);
    const hasANCFollowup2 = programEncounter.programEnrolment.getEncounters().some(enc => enc.name === 'ANC Follow-up Visit 2' && !enc.cancelDateTime);
    const hasANCFollowup3 = programEncounter.programEnrolment.getEncounters().some(enc => enc.name === 'ANC Follow-up Visit 3' && !enc.cancelDateTime);

    if (isLmpPresent && !programExited) {
        const lmpDate = moment(programEncounter.findObservationInEntireEnrolment('Last menstrual period').getValue());
        console.log('lmpDate', lmpDate, programEncounter);
        const trimesterOfRegistration = programEncounter.getObservationReadableValue('Trimester of Registration in govt ANC program');
        if (!hasDelivery) {
            let visitName, days;
            if (trimesterOfRegistration === "First" && !hasANCFollowup1) {
                visitName = "ANC Follow-up Visit 1";
                days = [84, 98];
            } else if (trimesterOfRegistration === "Second" && !hasANCFollowup2) {
                visitName = "ANC Follow-up Visit 2";
                days = [175, 189];
            } else if (trimesterOfRegistration === "Third" && !hasANCFollowup3) {
                visitName = "ANC Follow-up Visit 3";
                days = [235, 250];
            }
            if (visitName) {
                scheduleBuilder.add({
                    name: visitName,
                    encounterType: "ANC Follow-up Visit",
                    earliestDate: lmpDate.clone().add(days[0], 'days').toDate(),
                    maxDate: lmpDate.clone().add(days[1], 'days').toDate()
                });
            }
        }
    }
    const highRiskConditions = [
        {concept: "Height", operator: "lessThan", value: 145},
        {concept: "BMI", operator: "lessThan", value: 18.5},
        {concept: "BMI", operator: "greaterThan", value: 30},
        {concept: "Systolic", operator: "greaterThan", value: 140},
        {concept: "Diastolic", operator: "greaterThan", value: 90},
        {concept: "Number of abortions", operator: "greaterThan", value: 2},
        {concept: "Gravida", operator: "greaterThan", value: 4},
        {concept: "Number of stillbirths", operator: "greaterThan", value: 0},
        {concept: "Complications in past pregnancy", operator: "containsAnswerConceptNameOtherThan", value: "None"},
        {concept: "Other High-Risk Condition in previous pregnancy", operator: "defined"},
        {concept: "Complications in Current Pregnancy", operator: "containsAnswerConceptNameOtherThan", value: "None"},
        {concept: "Lifestyle Factors", operator: "containsAnswerConceptName", value: "Alcohol"},
        {concept: "Lifestyle Factors", operator: "containsAnswerConceptName", value: "Dhatura"},
        {concept: "Lifestyle Factors", operator: "containsAnswerConceptName", value: "Drug abuse"},
        {concept: "Lifestyle Factors", operator: "containsAnswerConceptName", value: "Other substance abuse"},
        {concept: "Medical history", operator: "containsAnswerConceptNameOtherThan", value: "None"},
        {concept: "Other Chronic Condition", operator: "defined"},
        {concept: "Other High Risk Condition in current pregnancy", operator: "defined"},
    ];

    const isHighRisk = isAgeHighRisk || highRiskConditions.some(condition => {
        const ruleCondition = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter(condition.concept);

        return condition.operator === "defined"
            ? ruleCondition.defined.matches()
            : ruleCondition[condition.operator](condition.value).matches();
    });

    const hasHighRiskPregnancyEnrolment = programEncounter.programEnrolment.getEncounters().some(enc => enc.name === 'MH High Risk Pregnancy Enrolment' && !enc.cancelDateTime);

    if (isHighRisk && !hasDelivery && !hasHighRiskPregnancyEnrolment && !programExited) {
        scheduleBuilder.add({
            name: "MH High Risk Pregnancy Enrolment",
            encounterType: "High Risk Enrolment",
            earliestDate: moment(programEncounter.encounterDateTime).add(3, 'days').toDate(),
            maxDate: moment(programEncounter.encounterDateTime).add(7, 'days').toDate()
        });
    }

    return scheduleBuilder.getAll();
};
