visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});
  const hasDelivery = programEncounter.programEnrolment.encounters.some(encounter =>
        encounter.encounterType.name === "Delivery Encounter" &&
        encounter.encounterDateTime &&
        !encounter.voided &&
        !encounter.cancelDateTime
  );
  const programExited = programEncounter.programEnrolment.programExitDateTime;
  const hasHighRiskFollowup = programEncounter.programEnrolment.getEncounters().some(enc => enc.name === 'MH High Risk Follow up' && !enc.cancelDateTime);
  if(!hasDelivery && !programExited && !hasHighRiskFollowup){
    const earliestDate = moment(programEncounter.encounterDateTime).add(7, 'days').toDate();
    const maxDate = moment(programEncounter.encounterDateTime).add(14, 'days').toDate();
    scheduleBuilder.add({name: "MH High Risk Follow up", encounterType: "High Risk Followups", earliestDate, maxDate});  
  }

  return scheduleBuilder.getAll();
};
