formElements.rule:
'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter, formElement}).when.valueInEncounter("c6e3d4a0-6ba8-45e0-a7a7-df96910b3ebe").containsAnswerConceptName("e7b50c78-3d90-484d-a224-9887887780dc").matches();

  visibility = condition11 ;

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("c6e3d4a0-6ba8-45e0-a7a7-df96910b3ebe").defined.matches();

  if(condition11 ){
    const earliestDate = moment(programEncounter.encounterDateTime).add(15, 'days').toDate();
    const maxDate = moment(programEncounter.encounterDateTime).add(17, 'days').toDate();
    scheduleBuilder.add({name: "Chronic Patient Follow up", encounterType: "Chronic Patient Follow up", earliestDate, maxDate});  
}

  return scheduleBuilder.getAll();
};
