visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("5a82b6ec-6fae-464a-8213-0f127052cfd1").defined.matches();

  const condition12 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("bc4933fc-818e-42fa-a47c-67d327485536").containsAnswerConceptNameOtherThan("e7b50c78-3d90-484d-a224-9887887780dc").matches();

  if(condition11 ){
    const earliestDate = moment(programEncounter.encounterDateTime).add(15, 'days').toDate();
    const maxDate = moment(programEncounter.encounterDateTime).add(17, 'days').toDate();
    scheduleBuilder.add({name: "Chronic Patient Follow up", encounterType: "Chronic Patient Follow up", earliestDate, maxDate});  
}
    if(condition12 ){
    const earliestDate = moment(programEncounter.encounterDateTime).add(1, 'days').toDate();
    const maxDate = moment(programEncounter.encounterDateTime).add(3, 'days').toDate();
    scheduleBuilder.add({name: "MMU Referral", encounterType: "MMU Referral", earliestDate, maxDate});  
}

  return scheduleBuilder.getAll();
};
