'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const formElement = params.formElement;
  const statusBuilder = new imports.rulesConfig.FormElementStatusBuilder({programEncounter, formElement});
  const painEpisodes = programEncounter.getObservationReadableValue('How many Pain episodes in the last 3 months (Not mild)');
  const severe = programEncounter.getObservationReadableValue('How many pain episodes were severe in last 3 minths');
  if (painEpisodes && severe && (painEpisodes < severe)) {
    statusBuilder.validationError('This number cannot be more than total pain episodes in last 3 months');
  }
  statusBuilder.show().when.valueInEncounter('How many Pain episodes in the last 3 months (Not mild)').greaterThan(0);
  return statusBuilder.build();
};

