'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter, formElement}).when.valueInCancelEncounter("0aeb78e6-e839-4299-8488-39e6df71e6ce").containsAnswerConceptName("05ea583c-51d2-412d-ad00-06c432ffe538").matches();

  visibility = condition11 ;

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

visitScheduleRule:
"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const moment = imports.moment;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});
    const ruleCondition = new imports.rulesConfig.RuleCondition({programEncounter});

    const isLmpPresent = ruleCondition.when.valueInEnrolment('Last menstrual period').defined.matches();

    const hasDelivery = programEncounter.programEnrolment.encounters.some(encounter =>
        encounter.encounterType.name === "Delivery Encounter" &&
        encounter.encounterDateTime &&
        !encounter.voided &&
        !encounter.cancelDateTime
    );

    const encounterType = programEncounter.encounterType.name;

    if (!hasDelivery && encounterType === "High Risk Followups") {
        scheduleBuilder.add({
            name: "MH High Risk Follow-up",
            encounterType: "High Risk Followups",
            earliestDate: moment(programEncounter.earliestVisitDateTime).add(30, 'days').toDate(),
            maxDate: moment(programEncounter.earliestVisitDateTime).add(37, 'days').toDate()
        });
    }

    if (isLmpPresent && !hasDelivery && encounterType === "ANC Follow-up Visit") {
        const lmpDate = moment(programEncounter.findObservationInEntireEnrolment('Last menstrual period').getValue());
        const trimesterOfRegistration = programEncounter.getObservationReadableValue('Trimester of Registration in govt ANC program');
        const currentEncounterName = programEncounter.name;
        const ancSchedules = [
            {current: "ANC Follow-up Visit 1", next: "ANC Follow-up Visit 2", days: 175, overdue: 189},
            {current: "ANC Follow-up Visit 2", next: "ANC Follow-up Visit 3", days: 235, overdue: 250},
            {current: "ANC Follow-up Visit 3", next: "ANC Follow-up Visit 4", days: 260, overdue: 275},
        ];
        const nextVisit = ancSchedules.find(visit => visit.current === currentEncounterName);
        if (nextVisit) {
            scheduleBuilder.add({
                name: nextVisit.next,
                encounterType: "ANC Follow-up Visit",
                earliestDate: lmpDate.clone().add(nextVisit.days, 'days').toDate(),
                maxDate: lmpDate.clone().add(nextVisit.overdue, 'days').toDate()
            });
        }
        if (currentEncounterName === "ANC Follow-up Visit 3") {
            scheduleBuilder.add({
                name: "Delivery Encounter",
                encounterType: "Delivery Encounter",
                earliestDate: lmpDate.clone().add(250, 'days').toDate(),
                maxDate: lmpDate.clone().add(280, 'days').toDate()
            });
        }
    }

    return scheduleBuilder.getAll();
};
