({params, imports}) => {
    const programEncounter = params.entity;
    const moment = imports.moment;
    const formElement = params.formElement;
    const _ = imports.lodash;
    let visibility = true;
    let value = null;
    let answersToSkip = [];
    let validationErrors = [];
    const encounterType = programEncounter.encounterType.name;
    const encounterDateTime = programEncounter.encounterDateTime;

    const previousEncounters = programEncounter.programEnrolment.getEncountersOfType(encounterType)
        .filter((enc)=>
            enc.encounterDateTime
            && enc.uuid != programEncounter.uuid
            && enc.earliestVisitDateTime
        )

    if(previousEncounters && previousEncounters.length > 0){
        const latestEncounter = previousEncounters[0];
        let layoutDate = latestEncounter.getObservationValue('e0935e96-4164-4161-9f7f-b88681628fa1');
        let cuurentLayoutDate = programEncounter.getObservationValue('e0935e96-4164-4161-9f7f-b88681628fa1');
        if(cuurentLayoutDate && layoutDate){
            layoutDate = moment(layoutDate).startOf('day');
            cuurentLayoutDate = moment(cuurentLayoutDate).startOf('day');
            if(!cuurentLayoutDate.isSame(layoutDate)) {
                validationErrors.push("Layout Date should be same through out the Orchard Development");
            }
        }
    }
    else {
        const today = moment().startOf('day');
        const todayMonth = today.month() + 1;

        const isAprilMay = todayMonth == 4 || todayMonth == 5;

        visibility = isAprilMay;

        let selectedDate = programEncounter.getObservationValue('e0935e96-4164-4161-9f7f-b88681628fa1'); // layoutDate

        if(selectedDate) {
            selectedDate = moment(selectedDate).startOf('day');
            if(selectedDate.isAfter(today)) {
                validationErrors.push("Selected Date Should not be in the future.");
            }
        }
    }

    return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

