"use strict";
({ params, imports }) => {
    const programEncounter = params.entity;
    const moment = imports.moment;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});

    const encounterDateTime = moment(programEncounter.encounterDateTime).startOf('day');
    const jan1st = moment().startOf('year').startOf('day');
    const june1st = moment().startOf('year').add(5, 'months').startOf('day');
    const dec1st = moment().startOf('year').add(11, 'months').startOf('day');
    const nextYearJune1st = moment().startOf('year').add(1, 'year').add(5, 'months').startOf('day');

    let earliestDate = null;

    if(encounterDateTime.isBefore(june1st) && encounterDateTime.isSameOrAfter(jan1st)) {
        earliestDate = june1st;
    } else if(encounterDateTime.isSameOrAfter(june1st) && encounterDateTime.isBefore(dec1st)) {
        earliestDate = dec1st;
    } else if(encounterDateTime.isSameOrAfter(dec1st)) {
        earliestDate = nextYearJune1st;
    }

    const isSurvivalRateAlreadyScheduled = programEncounter.programEnrolment.getEncountersOfType("Survival Rate").length > 0;

    const isProgramActive = programEncounter.programEnrolment.programExitDateTime == null;

    if(earliestDate && !isSurvivalRateAlreadyScheduled && isProgramActive){
        earliestDate = earliestDate.toDate();
        const maxDate = moment(earliestDate).add(30, 'days').endOf('day').toDate();
        scheduleBuilder.add({name: "Survival Rate Follow-up 1", encounterType: "Survival Rate", earliestDate, maxDate});  
    }

    // Orchard Management Visit Schedule
    function getNextQuarterDate(currentDate) {
        const currentYear = currentDate.getFullYear();
        const currentMonth = currentDate.getMonth(); // 0-based: 0=Jan, 2=Mar, 5=Jun, 8=Sep, 11=Dec

        const quarters = [
            { month: 2, day: 1, name: "March 1st" },   // Q1
            { month: 5, day: 1, name: "June 1st" },   // Q2
            { month: 8, day: 1, name: "September 1st" }, // Q3
            { month: 11, day: 1, name: "December 1st" }  // Q4
        ];

        for (const quarter of quarters) {
            const quarterDate = new Date(currentYear, quarter.month, quarter.day);

            if (currentDate < quarterDate) {
                return quarterDate;
            }
        }
    }

    const nextQuarterDate = getNextQuarterDate(programEncounter.encounterDateTime);

    const isOrchardManagementAlreadyScheduled = programEncounter.programEnrolment.getEncountersOfType("Orchard Management").length > 0;

    earliestDate = null;
    if(!isOrchardManagementAlreadyScheduled && nextQuarterDate && isProgramActive){
        earliestDate = moment(nextQuarterDate).startOf('month').toDate();
        const maxDate = moment(earliestDate).add(30, 'days').endOf('day').toDate();
        scheduleBuilder.add({name: "Orchard Management Follow-up 1", encounterType: "Orchard Management", earliestDate, maxDate});  
    }

    return scheduleBuilder.getAll();
};
