'use strict';
({params, imports}) => {
  const individual = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  const condition11 = new imports.rulesConfig.RuleCondition({individual, formElement}).when.valueInRegistration("c9d06045-03f6-45c9-a137-571a6263a5bc").defined.and.when.valueInRegistration("8be4e675-ec85-4945-8608-866061150db5").defined.and.when.valueInRegistration("5f5ae152-7fd7-4322-bfc7-584bc10dd8f9").defined.matches();

  if(condition11 ){
    const maleCount = individual.getObservationReadableValue('c9d06045-03f6-45c9-a137-571a6263a5bc');
    const femaleCount = individual.getObservationReadableValue('8be4e675-ec85-4945-8608-866061150db5');
    const childrenCount = individual.getObservationReadableValue('5f5ae152-7fd7-4322-bfc7-584bc10dd8f9');
    value = maleCount + femaleCount + childrenCount;
}

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

'use strict';
({params, imports}) => {
  const individual = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];
  let totalHouseholdMemberObservation = individual.findObservation('b1b200fb-df33-466e-abde-3e38c90b9992');
  let totalHouseholdMemberValue = _.isEmpty(totalHouseholdMemberObservation) ? totalHouseholdMemberObservation : totalHouseholdMemberObservation.getReadableValue();
  const condition11 = new imports.rulesConfig.RuleCondition({individual, formElement}).when.valueInRegistration("17dc73d7-e11f-4512-8fc8-5d3eb984ceb9").defined.and.when.valueInRegistration("b1b200fb-df33-466e-abde-3e38c90b9992").defined.and.when.valueInRegistration("17dc73d7-e11f-4512-8fc8-5d3eb984ceb9").greaterThan(totalHouseholdMemberValue).matches();

  if(condition11 ){
    validationErrors.push("Number of waste pickers in the family cannot be greater than total household member");  
}

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};

