'use strict';
({params, imports}) => {
    return params.db.objects('ProgramEncounter')
        .filtered(`
            programEnrolment.individual.voided = false 
            AND programEnrolment.voided = false 
            AND programEnrolment.program.name = 'Social Security' 
            AND programEnrolment.programExitDateTime = null 
            AND voided = false 
            AND encounterDateTime <> null 
            AND encounterType.name = 'Avail Service'`)
        .filtered('TRUEPREDICATE sort(programEnrolment.individual.uuid asc , encounterDateTime desc)')
        .filter(enc => {
            const isSchemeSubmitted = enc.getObservationReadableValue('16985f7c-1f02-43a5-838e-b9555b035b56') === 'Yes';
            if(isSchemeSubmitted){
                const isSchemeDone = enc.getObservationReadableValue('e49e413f-bdcb-477f-bf79-bbeaf61d3fd3') === 'Yes';
                return !isSchemeDone;
            }
            return false;
        })
        .map(enc => enc.programEnrolment.individual)
};

